<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Login extends MY_Controller {       public function __Construct(){		  parent:: __Construct();		  }	public function index()	{		$this->load->view('Admin/login');	}	public function login_check(){	      $userdata = array("email"=>$this->input->post('email'),	                        "password"=>$this->input->post('password'));	      $response  = $this->Admin_model->login_admin($userdata);	      if($response[0]->admin_role == 'admin'){	            $this->session->set_userdata('sess_data',$response);	            if($this->session->userdata('sess_data')){	                redirect('Admin/Dashboard');	            }	          }	        if(($response[0]->admin_role =='subadmin') && ($response[0]->admin_status =='1')){	             $this->session->set_userdata('subadmin_data',$response);	            if($this->session->userdata('subadmin_data')){	                redirect('Admin/SubadminDashboard');	            }	        }	       else{	           redirect('Admin/Login');	       }    	}	public function logout_admin(){	    $this->session->unset_userdata('sess_data');	    $this->session->sess_destroy();	    redirect('Admin/Login');	}		public function logout(){	    $this->session->unset_userdata('sess_data');	    $this->session->sess_destroy();	    redirect('Admin/Login');	}		public function logout_subadmin(){	    $this->session->unset_userdata('subadmin_data');	    $this->session->sess_destroy();	    redirect('Admin/Login');	}}